package com.sk.domain;import java.io.Serializable;import javax.persistence.Embeddable;@Embeddablepublic class Photo implements Serializable {	private static final long serialVersionUID = 7971969835944982700L;		private String fileName;	public String getFileName() {		return fileName;	}	public void setFileName(String fileName) {		this.fileName = fileName;	}	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((fileName == null) ? 0 : fileName.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj)			return true;		if (obj == null)			return false;		if (getClass() != obj.getClass())			return false;		Photo other = (Photo) obj;		if (fileName == null) {			if (other.fileName != null)				return false;		} else if (!fileName.equals(other.fileName))			return false;		return true;	}			}
