// -------------------------------// Kevin T. Manley// CSE 593// -------------------------------package serversrc.resImpl;import java.util.*;// A specialization of Hashtable with some//  extra diagnosticspublic class RMHashtable extends Hashtable{    RMHashtable() {      super();    }        /**	 * deeply clones a Map by cloning all the values.	 */    static RMHashtable deepCopy(RMHashtable ht){    	RMHashtable copy = new RMHashtable();	    for(Enumeration e = ht.keys(); e.hasMoreElements(); ) {	    	Object key = e.nextElement();	        copy.put(key, ht.get(key));	    }    	return copy;    }    @Override    public String toString()    {    	String s = "--- BEGIN RMHashtable ---\n";    	Object key = null;    	for (Enumeration e = keys(); e.hasMoreElements(); ) {    		key = e.nextElement();    		String value = (String)get( key );    		s = s + "[KEY='"+key+"']" + value + "\n";    	}    	s = s + "--- END RMHashtable ---";    	return s;    }    public void dump()    {    	System.out.println( toString() );    }}
