package aigilas.hud;import org.newdawn.slick.Color;import spx.core.Depth;import spx.core.GameManager;import spx.core.Point2;import spx.core.XnaManager;import aigilas.creatures.ICreature;import aigilas.creatures.StatType;import aigilas.management.Commands;public class SkillHud extends IHud {	private static String __separator = "|";	private Point2 _manaPosition = new Point2();	public SkillHud(ICreature owner) {		super(owner, GameManager.SpriteWidth, XnaManager.WindowHeight / 4);		_manaPosition = new Point2(GetHudOrigin().X, GetHudOrigin().Y				+ XnaManager.WindowHeight / 4);	}	private int CalculateHeight(String statType) {		return (int) ((_parent.Get(statType) / _parent.GetMax(statType)) * _dimensions.Y);	}	private int CostOfCurrentSkill() {		return (int) (_parent.GetCurrentSkillCost()				/ _parent.GetMax(StatType.MANA) * _dimensions.Y);	}	private String GetSkillStrings() {		return "A:" + _parent.GetActiveSkillName() + __separator + "X:"				+ _parent.GetHotSkillName(Commands.HotSkill1) + __separator				+ "Y:" + _parent.GetHotSkillName(Commands.HotSkill2)				+ __separator + "B:"				+ _parent.GetHotSkillName(Commands.HotSkill3) + __separator;	}	public void Update() {		if (_isVisible) {			_textHandler.Update();			_textHandler.Clear();			_textHandler.WriteDefault(GetSkillStrings(),					GameManager.SpriteWidth, 0, GetHudOrigin());		}	}	public void Draw() {		if (!_isVisible) {			return;		}		XnaManager.Renderer.Draw(_menuBase.getScaledCopy((int) _dimensions.X,				CalculateHeight(StatType.HEALTH)), GetHudOrigin(), Depth.HudBG,				Color.green);		XnaManager.Renderer.Draw(_menuBase.getScaledCopy((int) _dimensions.X,				CalculateHeight(StatType.MANA)), _manaPosition, Depth.HudBG,				Color.blue);		XnaManager.Renderer.Draw(_menuBase.getScaledCopy(				(int) (_dimensions.X * .5f), CostOfCurrentSkill()),				_manaPosition, Depth.HudBG, Color.yellow);		_textHandler.Draw();	}}
